import React from 'react';
import { Box, Typography, makeStyles }
  from '@material-ui/core';
import NavBar from './components/navBar/index';
import BGImage from './images/arif-wahid-y3FkHW1cyBE-unsplash.png';
import { useNeumorphShadowStyles } from '@mui-treasury/styles/shadow/neumorph';


const useStyles = makeStyles(theme => ({
  root: {
    height: '100vh',
    flexGrow: 1,
    backgroundColor: theme.palette.secondary.main,
    // backgroundImage: `url(${BGImage})`,
    backgroundSize: 'cover',
    overflow: 'hidden',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    marginTop: '100px',
    display: "flex",
    justifyContent: "center",
    color: 'white'
  },
  textBox: {
    display: 'flex',
    alignItems: 'center'
  }
}));

const App = () => {
  // const classes = useStyles();
  const [bgColor, setBgColor] = React.useState('#B3D2E6');
  const classes = useNeumorphShadowStyles({ bgColor });

  return (
    <Box >
      <NavBar />
      <Box
        p={5}
        bgcolor={bgColor}
        width={'100%'}
        height={'100%'}
        display={'flex'}
        justifyContent={'center'}
        alignItems={'center'}
        flex={1}
      >
        <Box classes={classes} borderRadius={16} width={240} height={160} />
      </Box>
    </Box>
  );
}

export default App;
