import { createMuiTheme } from '@material-ui/core';

export const lightTheme = createMuiTheme({
  palette: {
    type: 'light',
    primary: {
      main: '#fafafa'
    },
    secondary: {
        main: '#4d84f1'
    },
    aboutDrawer: {
      main: '#f5f5f5'
    },
    textPrimary: {
      main: '#212121'
    },
    backgroundColor: {
      box: 'rgb(194, 223, 235) -10px -10px 16px 0px, rgb(122, 167, 205) 10px 10px 16px 0px, rgb(194, 223, 235) 2px 2px 4px 0px inset, rgb(122, 167, 205) -2px -2px 4px 0px inset'
    },
  }
});
